def.body:
{% if flag?(:apple) %}
      metal_layer = LibPlatform.get_metal_layer Glfw.get_cocoa_window(window)
      return WGPU::Surface.from_metal_layer("#{name} Metal Layer", metal_layer)
    {% end %}
{% if flag?(:linux) %}
      return WGPU::Surface.from_xlib("#{name} X11 Window", Glfw.get_x11_display, Glfw.get_x11_window(window))
    {% else %}
      abort("Unsupported platform!", 1)
    {% end %}

Glfw.get_window_size(@handle, out width, out height)
@width = UInt16.new(width)
@height = UInt16.new(height)
{width: Int32.new(@width), height: Int32.new(@height)}

if @swap_chain.nil? || (@old_size != self.size)
  @old_size = {width: @width, height: @height}
  surface = @surface.not_nil!
  if surface.is_valid?
  else
    abort("Window surface is not valid", 1)
  end
  future_swap_chain_desc = future do
    SwapChainDescriptor.from_window(self, surface.preferred_format.not_nil!.get)
  end
  @swap_chain = @device.not_nil!.create_swap_chain(@surface.not_nil!, future_swap_chain_desc.get)
end
